#!/usr/bin/perl

$string = "



";

<<E
$string = "ULL
RRDDD
LURDL
UUUUD";
E
;

@lines = split(/\n/, $string);

%keys = (
  "-1,1" => 1, "0,1" => 2, "1,1" => 3,
  "-1,0" => 4, "0,0" => 5, "1,0" => 6,
  "-1,-1" => 7, "0,-1" => 8, "1,-1" => 9 );

$x = 0;
$y = 0;
$res = "";

foreach $line (@lines) {
  @dirs = split(//, $line);

  foreach $dir (@dirs) {
    if ($dir eq "R" and $x < 1) { $x++; }
    elsif ($dir eq "U" and $y < 1) { $y++; }
    elsif ($dir eq "L" and $x > -1) { $x--; }
    elsif ($dir eq "D" and $y > -1) { $y--; }
    print "Current pos: $dir: $x,$y: ", $keys{"$x,$y"}, "\n";
  }
  print "Final pos: $x,$y: ", $keys{"$x,$y"}, "\n";
  $res = $res . $keys{"$x,$y"};
}

print "Result: $res\n";


