#!/usr/bin/perl

$string = "



";

<<E
$string = "ULL
RRDDD
LURDL
UUUUD";
E
;

@lines = split(/\n/, $string);

%keys = (
                          "2,2" => 1,
              "1,1" => 2, "2,1" => 3, "3,1" => 4,
  "0,0" => 5, "1,0" => 6, "2,0" => 7, "3,0" => 8, "4,0" => 9,
            "1,-1" => 'A', "2,-1" => 'B', "3,-1" => 'C',
                          "2,-2" => 'D'
  );

$x = 0;
$y = 0;
$res = "";

foreach $line (@lines) {
  @dirs = split(//, $line);

  foreach $dir (@dirs) {
    if ($dir eq "R" and defined($keys{$x+1 . "," . $y})) { $x++; }
    elsif ($dir eq "U" and defined($keys{$x . "," . ($y+1)})) { $y++; }
    elsif ($dir eq "L" and defined($keys{($x-1) . "," . $y})) { $x--; }
    elsif ($dir eq "D" and defined($keys{$x . "," . ($y-1)})) { $y--; }
    print "Current pos: $dir: $x,$y: ", $keys{"$x,$y"}, "\n";
  }
  print "Final pos: $x,$y: ", $keys{"$x,$y"}, "\n";
  $res = $res . $keys{"$x,$y"};
}

print "Result: $res\n";


